const Task = ({task, onTaskDelete, toggleReminder }) => {
  return (
    <div className={`task ${task.reminder ? 'reminder': '' }`} onDoubleClick={() => toggleReminder(task.id)}>
        <h3 className="justification">
            {task.text} <span className="times_icon" style={{color: 'red', cursor: 'pointer'}} onClick={() =>onTaskDelete(task.id)} >x</span>
        </h3>
        <p>{task.day}</p>
    </div>
  )
}

export default Task
