import './App.css';
import Footer from './Components/Footer';
import Header from './Components/Header';
import { useState } from 'react';
import Form from './Components/Form';
import Data from './Components/Data';

function App() {

  const [ showAddForm, setAddForm ] = useState(false)
  const [data, setData] = useState([
    {
      id: 1,
      name: 'Brevin',
      email: 'food@gmail.com',
      favioret: 'food'
    },
    {
      id: 2,
      name: 'Kiggundu',
      email: 'kiggundu@gmail.com',
      favioret: 'Africa'
    },
    {
      id: 3,
      name: 'Kiggundu Brevin',
      email: 'kiggundu@gmail.com',
      favioret: 'Uganda'
    }
  ])

  return (
    <div className='body d-flex flex-column'>
      <Header onAdd={() => setAddForm(!showAddForm)} onShow={showAddForm}/>
      <div className='container'>
        {showAddForm && <Form/>}

        {/* {datas.map((data) => (<Data key={data.id}/>)) } */}
        <h3 className="justification" key={data.id}>
            {data.Name} <span className="times_icon" style={{color: 'red', cursor: 'pointer'}}>x</span>
        </h3>
        <p>{data.email} {data.favioret}</p>

      </div>
      <Footer/>
    </div>
  );
}

export default App;
