import Header from './components/Header'
import Tasks from './components/Tasks'
import AddTask from './components/AddTask';
import { useState } from 'react'

import './App.css';

function App() {
  const [showAddTask, setShowAddTask] = useState(false)

  const [tasks, setTasks] = useState([
    {
      id: 1,
      text: 'Doctors Appointment',
      day: 'Feb 5th at 2:30pm',
      reminder: true
    },
    {
      id: 2,
      text: 'Court Appearance',
      day: 'Feb 15th at 3:30pm',
      reminder: true
    },
    {
      id: 3,
      text: 'Parents-Teacher meeting',
      day: 'Feb 7th at 7:30pm',
      reminder: false
    }
  ])

  // Delete function
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id != id))
  }

  // Toggle reminder function
  const onToggleReminder = (id) => {
    setTasks(tasks.map((task) => task.id === id ? {...task, reminder: !task.reminder} : task))
  }

  // Add Task
  const addTask = (task) => {
    const id = Math.floor(Math.random() * 1000) + 1
    const newTask = {id, ...task }
    setTasks([...tasks, newTask])
  }

  return (
   <div className='container'>
    <Header onAdd = {() => setShowAddTask(!showAddTask)} showAdd={showAddTask} />

    {showAddTask && <AddTask onAdd={addTask} />}

    {tasks.length > 0 ? (<Tasks tasks={tasks} onTaskDelete={deleteTask} toggleReminder={onToggleReminder} />) 
    : (
      'No task to show'
    )}
   </div> 
  );
}

export default App;
